#include<bits/stdc++.h>
using namespace std;

/*A = "scaler"
 B = 2*
 erscal */

string solve(string A, int B) 
{
    int n =A.size();
    char * C ;
    C = new char[n+1];
    int j=0;
   // cout <<" n ="<<n<<endl;
   
   if (n <2)
      {
          return A;
      }
    
    int k= B%n;
    
        for(int i =n-k ;i < n ;i ++)
        {
            C[j] = A[i];
            j++;
        }
        for(int i=0;i < n-k;i++)
        {
             C[j] = A[i];
             j++;
        }
        C[j] ='\0';
  cout <<"C [j] = "<<C<<endl;;
    return C;
    
}




int main()
{
    string S="...";
    solve(S, 2);
    cout <<"S ="<<S<<endl;
}
 
 
